import React, { Component } from 'react';

class CharacterButton extends Component {
  constructor(props) {
    super(props);
    this.state = {
      clicked: false,
      character: props.character
    };
    this.handleClick = this.handleClick.bind(this);
    this.resetClickedState = this.resetClickedState(this);
  }

  async resetClickedState() {
    await this.setState({clicked: false});
  }

  handleClick() {
    const {endgame, tried, character} = this.props;
    if (endgame || tried.indexOf(character) !== -1 ) {
      return;
    }
    this.setState(prevState => ({
      clicked: !prevState.clicked
    }));
//    this.props.onClick(!this.state.clicked);
  }

  render() {
    const { character, clicked } = this.state;
    const bgColor = clicked ? '#00FF00' : '#FFFFFF';

    return (
      <button 
        type="submit"
        value={character}
        onClick={this.handleClick}
        style={{backgroundColor: bgColor}}
        >
        <svg width="50" height="50">
         <text x="25" y="35">{character}</text>
        </svg>
        {clicked? (<h3>Clicked True</h3>) :
          (<h3>Clicked false</h3>) }
      </button>
    );
  }
}

export default CharacterButton;
