import React from 'react';
import dictionary from '../assets/dictionary.txt';

class FileLineReader extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lines: [],
      isStartTriggered: false
    };
  }

  componentDidMount() {
    const arr = dictionary.split('\n');
    this.setState({ lines: arr });
  }

  render() {
    return (
      <ul>
        <h1>{this.state.isStartTriggered ? 'Yes' : 'No'}</h1>
        {this.state.lines.map((line, index) => (
          <li key={index}>{line}</li>
        ))}
      </ul>
    );
  }
}

export default FileLineReader;
