import React, { Component } from 'react';
import CharacterButton from './CharacterButton';
import CharacterButtonRow from './CharacterButtonRow';

class AlphabetForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      formData: ['', '', '', '', '', ''],
    };
  }

  handleCharacterClick = (character) => {
    const { formData } = this.state;
    const updatedFormData = [...formData];
    const rowIndex = updatedFormData.findIndex((row) => row.indexOf(character) !== -1);

    if (rowIndex === -1) {
      const emptyRowIndex = updatedFormData.findIndex((row) => row === '');
      if (emptyRowIndex !== -1) {
        updatedFormData[emptyRowIndex] = character;
      }
    } else {
      const columnIndex = updatedFormData[rowIndex].indexOf(character);
      updatedFormData[rowIndex] = updatedFormData[rowIndex].slice(0, columnIndex) + updatedFormData[rowIndex].slice(columnIndex + 1);
    }

    this.setState({ formData: updatedFormData });
  };

  render() {
    const { formData } = this.state;
    const alphabet = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';

    return (
      <form>
        <CharacterButtonRow characters={alphabet.slice(0, 4).split('')} onClick={this.handleCharacterClick} />
        <CharacterButtonRow characters={alphabet.slice(4, 8).split('')} onClick={this.handleCharacterClick} />
        <CharacterButtonRow characters={alphabet.slice(8, 12).split('')} onClick={this.handleCharacterClick} />
        <CharacterButtonRow characters={alphabet.slice(12, 16).split('')} onClick={this.handleCharacterClick} />
        <CharacterButtonRow characters={alphabet.slice(16, 20).split('')} onClick={this.handleCharacterClick} />
        <CharacterButtonRow characters={alphabet.slice(20).split('')} onClick={this.handleCharacterClick} />

        <div>
          <label>
            Form Data:
            <textarea value={formData.join('\n')} readOnly />
          </label>
        </div>
      </form>
    );
  }
}

export default AlphabetForm;
