import React from 'react';
import fs from '../assets/dictionary.txt';

class FileLineReader extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lines: [],
      isStartTriggered: "No"
    };
  }

  componentDidMount() {
    const arr = [];
    fetch(fs)
      .then(r => r.text())
      .then(text => {
        const arr = [];
        const lines = text.split('\n');
        for (let i = 0; i < lines.length; i++) {
          const line = lines[i].replace(/\r?\n|\r/g, '');
          if (this.state.isStartTriggered) {
            if ( line.length == 5 ) {
              arr.push(line);
            };
          }
          if (line === 'START') {
            this.setState({ isStartTriggered: "No" });
          }
        }
        this.setState({ lines: arr }); 
      })
      .then(() => {
        console.log(arr);
      })
  }

  render() {
    return (
      <ul>
        <h1>{this.state.isStartTriggered}</h1>
        {this.state.lines.map((line, index) => (
          <li key={index}>{line}</li>
        ))}
      </ul>
    );
  }
}

export default FileLineReader;
