import React, { Component } from 'react';
import CharacterButton from './CharacterButton';
import CharacterButtonRow from './CharacterButtonRow';

class AlphabetForm extends Component {
  constructor(props) {
    super(props);
    this.props = props;
    this.buttonsRow = [
        {start: 0, end: 4},
        {start: 4, end: 8},
        {start: 8, end: 12},
        {start: 12, end: 16},
        {start: 16, end: 20},
        {start: 20, end: 24},
        {start: 24, end: 26},
      ]
    this.handleCharacterClick = this.handleCharacterClick.bind(this);
  }

  handleCharacterClick (event)  {
    event.preventDefault();
    const character = event.nativeEvent.submitter.attributes.value.value;
    this.props.onSubmit(character);
  };

  render() {
    const alphabet = 'abcdefghijklmnopqrstuvwxyz';
    const endgame = this.props.endgame;

    return (
      <form className="charinput" onSubmit={this.handleCharacterClick}>
        <CharacterButtonRow 
          characters={alphabet.slice(0, 4).split('')} 
          />
        <CharacterButtonRow 
          characters={alphabet.slice(4, 8).split('')}
          />
        <CharacterButtonRow characters={alphabet.slice(8, 12).split('')} />
        <CharacterButtonRow characters={alphabet.slice(12, 16).split('')} />
        <CharacterButtonRow characters={alphabet.slice(16, 20).split('')} />
        <CharacterButtonRow characters={alphabet.slice(20, 24).split('')} />
        <CharacterButtonRow characters={alphabet.slice(24).split('')} />
      </form>
    );
  }
}

export default AlphabetForm;
