import React, { Component } from 'react';
import CharacterButton from './CharacterButton';

class CharacterButtonRow extends Component {
  constructor(props) {
    super(props);
    // Don't need real time to modify the buttons.
    this.state = {
      characters: this.props.characters
    };
  }

  render() {
    const {endgame, tried, resetButton} = this.props;
    if (resetButton) {
      this.refs.button.setState({clicked: false});
    }
    return (
      <div>
        {resetButton? ( <h1>ResetButton: Rowtrue</h1> ):
          ( <h1>ResetButton: Rowfalse</h1> )}
        {this.state.characters.map((character) => (
          <CharacterButton
            key={character}
            character={character}
            endgame={endgame}
            tried={tried}
            ref="button"
          />
        ))}
      </div>
    );
  }
}

export default CharacterButtonRow;
