import React, { Component } from 'react';
import CharacterButton from './CharacterButton';

class CharacterButtonRow extends Component {
  constructor(props) {
    super(props);
    // Don't need real time to modify the buttons.
    this.state = {
      characters: this.props.characters
    };
  }

  render() {
    const {endgame, tried} = this.props;
    if (this.props.resetButton) {
      this.refs.button.setState({clicked: false});
    }
    return (
      <div>
        {this.state.characters.map((character) => (
          <CharacterButton
            key={character}
            character={character}
            endgame={endgame}
            tried={tried}
            ref="button"
          />
        ))}
      </div>
    );
  }
}

export default CharacterButtonRow;
