import React from 'react';
import fs from '../assets/dictionary.txt';

class FileLineReader extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lines: [],
      isStartTriggered: false
    };
  }

  componentDidMount() {
    const arr = [];
    fetch(fs)
      .then(r => r.text())
      .then(text => {
        arr.push(text);
      });
    this.setState({ lines: arr });
  }

  render() {
    return (
      <ul>
        {this.state.lines.map((line, index) => (
          <li key={index}>{line}</li>
        ))}
      </ul>
    );
  }
}

export default FileLineReader;
