import React, { Component } from "react";
import Hangman from "./Hangman";
import AlphabetForm from "./AlphabetForm";
import fs from '../assets/dictionary.txt';
import ArrayDisplay from './ArrayDisplay';

class HangmanGame extends Component {
  constructor(props) {
    super(props);
    this.state = {
      step: 0,
      wordObj: {}
    };
    this.handleCharacterClick = this.handleCharacterClick.bind(this);
  }

  componentDidMount() {
    this.getAWordFromDictionary(5);
  }

  handleCharacterClick(char) {
    console.log(char);
    let foundOne = false;
    wordObj = this.state.wordObj;
    for (let i = 0; i < wordObj.length ; i ++) {
      if (char === wordObj[i].character) {
        wordObj[i].found = true;
        foundOne = true;
      }
    }
    if (!foundOne) {
      this.setState((prevState) => ({ step: prevState.step + 1 }));
    }
  }

  getAWordFromDictionary(wordLength) {
    fetch(fs)
      .then(r => r.text())
      .then(text => {
        const arr = [];
        let isStartTriggered = false;
        const lines = text.split('\n');
        for (let i = 0; i < lines.length; i++) {
          const line = lines[i].replace(/\r?\n|\r/g, '');
          if (isStartTriggered) {
            if ( line.length === wordLength ) {
              arr.push(line);
            };
          }
          if (line === 'START') {
            isStartTriggered = true;
          }
        }
        return arr;
      })
      .then(arr => {
        const gameArray = [];
        const randomIndex = Math.floor(Math.random() * arr.length);
        const choice = arr[randomIndex];
        for (const char of choice) {
          gameArray.push({ found: false , character: char });
        }
        this.setState({ wordObj: gameArray }); 
      })
  }

  render() {

    const {step} = this.state ;

    return (
      <div className="game">
         <ArrayDisplay array={this.state.wordObj} />
        <Hangman step={step} />
        <AlphabetForm onSubmit={this.handleCharacterClick}/>
      </div>
    );
  }
}

export default HangmanGame;
