import React, { Component } from 'react';
import CharacterButton from './CharacterButton';
import CharacterButtonRow from './CharacterButtonRow';

class AlphabetForm extends Component {
  constructor(props) {
    super(props);
    this.props = props;
    this.handleCharacterClick = this.handleCharacterClick.bind(this);
  }

  handleCharacterClick (event)  {
    event.preventDefault();
    const character = event.nativeEvent.submitter.attributes.value.value;
    this.props.onSubmit(character);
  };

  render() {
    const alphabet = 'abcdefghijklmnopqrstuvwxyz';
    const clickEnabled = this.props.clickEnabled;

    return (
      <form className="charinput" onSubmit={this.handleCharacterClick}>
        <CharacterButtonRow characters={alphabet.slice(0, 4).split('')} clickEnabled={clickEnabled} />
        <CharacterButtonRow characters={alphabet.slice(4, 8).split('')} clickEnabled={clickEnabled} />
        <CharacterButtonRow characters={alphabet.slice(8, 12).split('')} clickEnabled={clickEnabled} />
        <CharacterButtonRow characters={alphabet.slice(12, 16).split('')} clickEnabled={clickEnabled} />
        <CharacterButtonRow characters={alphabet.slice(16, 20).split('')} clickEnabled={clickEnabled} />
        <CharacterButtonRow characters={alphabet.slice(20, 24).split('')} clickEnabled={clickEnabled} />
        <CharacterButtonRow characters={alphabet.slice(24).split('')} clickEnabled={clickEnabled} />
      </form>
    );
  }
}

export default AlphabetForm;
