import React from 'react';

class FileLineReader extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lines: [],
      isStartTriggered: false
    };
  }

  componentDidMount() {
    const fs = require('fs');
    fs.readFile(this.props.filename, 'utf8', (err, data) => {
      if (err) throw err;
      const lines = data.split('\n');
      const arr = [];
      let isStartTriggered = false;
      for (let i = 0; i < lines.length; i++) {
        const line = lines[i].replace(/\r?\n|\r/g, '');
        if (isStartTriggered) {
          arr.push(line);
        }
        if (line === 'START') {
          isStartTriggered = true;
        }
      }
      this.setState({ lines: arr });
    });
  }

  render() {
    return (
      <ul>
        {this.state.lines.map((line, index) => (
          <li key={index}>{line}</li>
        ))}
      </ul>
    );
  }
}

export default FileLineReader;
