import React from 'react';

class HiddenWordGame extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      hiddenAnswer: props.answer.toLowerCase(),
      guesses: [],
      numGuesses: 0
    };
    this.handleGuess = this.handleGuess.bind(this);
  }

  handleGuess(event) {
    const guess = event.target.value.toLowerCase();
    const { guesses, hiddenAnswer, numGuesses } = this.state;
    if (guesses.includes(guess)) {
      alert(`You already guessed ${guess}!`);
      return;
    }
    const newGuesses = [...guesses, guess];
    const newNumGuesses = numGuesses + 1;
    const hiddenAnswerArray = hiddenAnswer.split('');
    const newDisplayArray = hiddenAnswerArray.map(letter =>
      newGuesses.includes(letter) ? letter : '_'
    );
    const newDisplayString = newDisplayArray.join(' ');
    this.setState({
      hiddenAnswer: newDisplayString,
      guesses: newGuesses,
      numGuesses: newNumGuesses
    });
    if (newDisplayString.indexOf('_') === -1) {
      alert(`Congratulations, you guessed the word in ${newNumGuesses} guesses!`);
    }
  }

  render() {
    const { hiddenAnswer, numGuesses } = this.state;
    return (
      <div>
        <p>Number of guesses: {numGuesses}</p>
        <p>{hiddenAnswer}</p>
        <input type="text" maxLength="1" onChange={this.handleGuess} />
      </div>
    );
  }
}

export default HiddenWordGame;
