import React, { Component } from "react";
import Hangman from "./Hangman";
import ArrayDisplay from "./ArrayDisplay";
import AlphabetForm from "./AlphabetForm";
import FileLineReader from "./FileLineReader";

class HangmanGame extends Component {
  constructor(props) {
    super(props);
    this.state = {
      step: 0,
      wordObj: {}
    };
    this.handleCharacterClick = this.handleCharacterClick.bind(this);
  }

  handleCharacterClick(char) {
    this.setState((prevState) => ({ step: prevState.step + 1 }));
  }

  render() {

    const {step} = this.state ;

    return (
      <div className="game">
        <ArrayDisplay array={this.state.wordObj} />
        <Hangman step={step} />
        <AlphabetForm onSubmit={this.handleCharacterClick}/>
        <FileLineReader filename="../assets/dictionary.txt" />
      </div>
    );
  }
}

export default HangmanGame;
