import React from 'react';
import fs from '../assets/dictionary.txt';
import ArrayDisplay from './ArrayDisplay';

class FileLineReader extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lines: [],
      isStartTriggered: false
    };
  }

  componentDidMount() {
    const arr = [];
    fetch(fs)
      .then(r => r.text())
      .then(text => {
        const arr = [];
        const lines = text.split('\n');
        for (let i = 0; i < lines.length; i++) {
          const line = lines[i].replace(/\r?\n|\r/g, '');
          if (this.state.isStartTriggered) {
            if ( line.length == 5 ) {
              arr.push(line);
            };
          }
          if (line === 'START') {
            this.setState({ isStartTriggered: true });
          }
        }
        return arr;
      })
      .then(arr => {
        const gameArray = [];
        const randomIndex = Math.floor(Math.random() * arr.length);
        const choice = arr[randomIndex];
        for (const char of choice) {
          gameArray.push({ found: false, character: char });
        }
        this.setState({ lines: gameArray }); 
      })
  }

  render() {
    let arr = this.state.lines ;
    console.log("Arr:");
    console.log(arr);
    return (
      <ul>
        <ArrayDisplay array={this.state.lines} />
      </ul>
    );
  }
}

export default FileLineReader;
