import React, { Component } from 'react';

class RestartGame extends Component {
  constructor(props) {
    super(props);
    // Don't need real time to modify the buttons.
    this.props = props;
  }

  start() {
    return (
      <svg width="50" height="50">
        <polygon points="25,10 31.51,26.18 49.14,26.18
          35.56,37.82 42.08,54 25,44.36
          7.92,54 14.44,37.82
          0.86,26.18 18.49,26.18" fill="yellow" />
      </svg>
    )
  }

  render() {
    const onRestartGame = this.props.onRestartGame;
    return (
      <div>
        <h3>Restart the Game</h3>
	      <button onClick={(e) => onRestartGame(e, 0)}>
	       {this.start()} 
	      </button><br/>
	      <button onClick={(e) => onRestartGame(e, 2)}>
	        Restart Game in Middle Level
	      </button><br/>
	      <button onClick={(e) => onRestartGame(e, 4)}>
	        Restart Game in Harder Level
	      </button><br/>
      </div>
    );
  }
}

export default RestartGame;
